if(cti.store.pages.AppSettings === undefined) {
  cti.store.pages.AppSettings = {};
}

if(cti.store.pages.AppSettings.accuservAPIAddress === undefined) {
  cti.store.pages.AppSettings.accuservAPIAddress = "";
}

cti.store.pages.AppSettings.accuservAPIAddress = cti.store.schema.metadata.accuservurl;

var code = cti.store.pages.AppSettings.apiQRcode;
var apis = cti.store.webService.apiUrls;
var exist = false;

if(code) {
  for(var i in apis) {
    if(apis[i].value == code) {
      exist = true;
      break;
    }
  }

  if(exist) {
    cti.utils.callAction(
      "show-notification", {
        "name": "app_odometer",
        "item-type": "app-notification",
        "style": "Warning",
        "title": "Warning",
        "text": "This Web Service URL has already been scanned to this device. Please check the list below."
      }
    );
  } else {
    apis.push({"value" : code});
    cti.store.schema.metadata.accuservurl = code;
    cti.store.pages.AppSettings.accuservAPIAddress = cti.store.schema.metadata.accuservurl;
    cti.store.pages.AppSettings.apiQRcode = undefined;
  } 
} else {
  cti.utils.callAction(
    "show-notification", {
      "name": "app_odometer",
      "item-type": "app-notification",
      "style": "Warning",
      "title": "Warning",
      "text": "Please scan a QR Code before attempting to save."
    }
  );
}

console.clear();
