  "sActivityCategory" : cti.store.payloads.pauseTask.sActivityCategory,
  "sActivityType" : cti.store.payloads.pauseTask.sActivityType,
  "tEstimatedTime" : cti.store.payloads.pauseTask.tEstimatedTime,
