if(cti.store.jobs.selectedJob.aOTCStock == undefined) {
  cti.store.jobs.selectedJob.aOTCStock = [];
}

var otcStock = cti.store.jobs.selectedJob.aOTCStock;
var code = {{$data.$item}};
var exisits = false;

var codeObj = code;
codeObj.sLocation = cti.store.variables.selectedLocation.sLocation || "NOLOCATION";
codeObj.nOrderQty = 1;

for(var i in otcStock) {
  if(otcStock[i].sCode == codeObj.sCode) {
    if(otcStock[i].sLocation == codeObj.sLocation) {
      exisits = true;
      break;
    }
  }
}

if(!exisits) {
