$( "#otcLocationFetch" ).click(function() {
  cti.utils.callAction('call-actionflow',{'name':'OnlineStockLocations'});
});

cti.fnOrderOTCLocations();
$success();

// Load Spinner
cti.fnOpenSpinner('Loading Order Catalogue...');

cti.store.variables.selectedLocation = {{$data.$item}};

window.appManager.loadFromDatabase('otcdatafull', function (doc) {
  var fullStock = doc.data;
  var stock = [];
  var filter = cti.store.variables.supplierOTCFilter;

  for(var i in fullStock) {
    var stockItem = fullStock[i].sAttrib1.trim();

    if(filter === "" || filter === undefined) {
      stock.push(fullStock[i]);
    } else if(stockItem === filter) {
      stock.push(fullStock[i]);
    }
  }

  window.appManager.saveToDatabase("otcdata", stock, function(rev) {
      window.appManager.loadFromDatabase('otcdata', function (doc) {
        var otcStock = cti.store.jobs.selectedJob.aOTCStock;
        var selectedLocation = cti.store.variables.selectedLocation;

        var exists = false;

        for(var i in otcStock) {
          if(otcStock[i].sLocation == selectedLocation.sLocation) {
            exists = true;
            break;
          }
        }

        if(exists) {
          cti.store.variables.menuOption = "schedules";
        } else {
          if(selectedLocation.sOTCStockQuantity != 0) {
            cti.store.variables.menuOption = "schedules";
          } else {
            cti.store.variables.menuOption = "search";
          }
        }

        cti.store.variables.otcStockSearchResults = [];
        var count = 50;

        for(var i in doc.data) {
          if(count === 0) {
            break;
          } else {
            count--;
            cti.store.variables.otcStockSearchResults.push(doc.data[i]);
          }
        }

        cti.utils.callAction('go-to-page', { "name": "OTCCodes" });

        $success();
      }, function (reason, err) {
        cti.fnCloseSpinner();
        cti.utils.callAction('call-actionflow', { "name": "OnlineOTCCodes" });
      });
      $success();
    }, function(reason, e) {
      window.appManager.logError('Failed to save OTC Stock data - ' + reason, e);
      $success();
  });
}, function (reason, err) {
  cti.fnCloseSpinner();
  cti.utils.callAction('call-actionflow', { "name": "OnlineOTCCodes" });
});

delete cti.store.variables.showFullLocations;
delete cti.store.variables.jobLocations;

cti.utils.callAction('go-to-page', { "name": "OTCPurchases" });

var job = cti.store.jobs.selectedJob;

if(job.aOTCStock === undefined) {
  job.aOTCStock = [];
}

if(job.aOTCStock.length > 0) {
  cti.store.variables.poStock = true;
} else {
  cti.store.variables.poStock = false;
}

cti.utils.updatePage();

var suppliers = cti.store.staticData.stockSuppliers;
var selectedSupplier = cti.store.pages.OTCPurchases.orderSupplier.value.replace(/ /g,'');
var deliveryMethod = cti.store.pages.OTCPurchases.orderDeliveryMethod;
var depots = [];

for(var i in suppliers) {
  var thisSupplier = suppliers[i];
  var supplierName = thisSupplier.sName.replace(/ /g,'');

  if(supplierName === selectedSupplier) {
    var depotsOnSupplier = thisSupplier.aSupplierDepots;

    if(depotsOnSupplier.length > 0) {
      for(var x in depotsOnSupplier) {
        var thisDepot = depotsOnSupplier[x];

        var depot = {
          "sAddressLine1" : thisDepot.sAddressLine1.trim() + ', ' + thisDepot.sPostcode.trim(),
          "sDepNo" : thisDepot.sDepNo.replace(/ /g,'')
        }

        depots.push(depot);
      }
    } else {
      var dummyDepot = {
        "sAddressLine1" : "NO DEPOTS AVAILABLE",
        "sDepNo" : "0"
      }

      depots.push(dummyDepot);
    }
    break;
  }
}

cti.store.staticData.stockSupplierDepots = depots;
cti.store.pages.OTCPurchases.orderDepot = undefined;

cti.utils.updatePage();

var suppliers = cti.store.staticData.stockSuppliers;
supplierSelected = "";

if(cti.store.pages.OTCPurchases != undefined) {
  if(cti.store.pages.OTCPurchases.orderSupplier != undefined) {
	var value = cti.store.pages.OTCPurchases.orderSupplier.value;
    supplierSelected = value.replace(/ /g,'');
  }
}

for(var i in suppliers) {
  var thisSupplier = suppliers[i];
  var thisSupplierName = thisSupplier.sName.replace(/ /g,'');

  if(thisSupplierName === supplierSelected) {
    cti.store.variables.supplierOTCFilter = thisSupplier.sAttrib1.trim();
    break;
  }
}


if(cti.store.variables.supplierOTCFilter != undefined) {
  cti.utils.callAction('go-to-page', { "name": "OTCLocationSelect" });
} else {
  cti.utils.callAction(
    "show-notification", {
      "name" : "app_sorcodematch",
      "item-type" : "app-notification",
      "style" : "error",
      "title" : "Warning",
      "text" : "You must select a Supplier from the list above before adding any Catalogue stock to your Purchase Order."
    }
  );
}

delete cti.store.jobs.selectedJob.aOTCStock;
delete cti.store.jobs.selectedJob.aPurchaseOrder;

cti.utils.destroyPageData('OTCPurchases');
cti.store.variables.specialOrder = false;
