if(cti.store.jobs.selectedJob.aPurchaseOrder == undefined) {
  cti.store.jobs.selectedJob.aPurchaseOrder = {};
}

if(cti.store.jobs.selectedJob.aOTCStock == undefined) {
  cti.store.jobs.selectedJob.aOTCStock = [];
}

var job = cti.store.jobs.selectedJob;
var selectedSupplier = cti.store.pages.OTCPurchases.orderSupplier.value.replace(/ /g,'');
var suppliers = cti.store.staticData.stockSuppliers;

for(var i in suppliers) {
  if(selectedSupplier == suppliers[i].sName.replace(/ /g,'')) {
    var supplierCode = suppliers[i].sAccountCode1.replace(/ /g,'');
    break;
  }
}

var purchaseOrder = {
  "sOperativeID": job.aOperativeDetail.sOperativeID,
  "sSupplierCode": supplierCode,
  "dOrderDate": moment().format('YYYY-MM-DD'),
  "dDeliveryDate": moment(cti.store.pages.OTCPurchases.orderDeliveryDate).format('YYYY-MM-DD'),
  "sDeliveryMethod": cti.store.pages.OTCPurchases.orderDeliveryMethod.value,
  "sSpecicalInstructions": cti.store.pages.OTCPurchases.orderDescription,
  "sCollectionDepNo": cti.store.pages.OTCPurchases.orderDepot.value,
  "aAccuServJobID": job.aAccuServJobID,
  "aAccuServPOItems": job.aOTCStock
};


if(purchaseOrder.aAccuServPOItems.length > 0) {
  cti.store.jobs.selectedJob.aPurchaseOrder = purchaseOrder;
  cti.store.payloads.purchaseOrderPayload = purchaseOrder;
  cti.utils.callAction('call-actionflow', { "name": "PurchaseOrder" });
} else {
  cti.utils.callAction(
    "show-notification", {
      "name" : "app_sorcodematch",
      "item-type" : "app-notification",
      "style" : "error",
      "title" : "Warning",
      "text" : "You must add at least 1 OTC Stock Item to the Purchase Order."
    }
  );
}

var lastPage = cti.store.variables.lastPOPage;

if(lastPage == "VanLocationSelect") {
  cti.fnNavigateVanStock(cti.store.state.currentPage);
} else {
  cti.utils.callAction('go-to-page', { "name": lastPage });
}

cti.fnOpenSpinner('Adding Van Stock...');

