  cti.store.jobs.selectedJob.aVanStock = [];
}

var vanStock = cti.store.jobs.selectedJob.aVanStock;
var code = {{$data.$item}};
var exisits = false;

var codeObj = code;
codeObj.sLocation = cti.store.variables.selectedLocation.sLocation || "NOLOCATION";
codeObj.nQtyUsed = 1;

for(var i in vanStock) {
  if(vanStock[i].sStockCode == codeObj.sStockCode) {
    if(vanStock[i].sLocation == codeObj.sLocation) {
      exisits = true;
      break;
    }
  }
}

if(!exisits) {
  vanStock.unshift(codeObj);

  cti.fnCloseSpinner();
  cti.utils.callAction(
    "show-notification", {
      "name" : "app_sorcodeadded",
      "item-type" : "app-notification",
      "style" : "success",
      "title" : "Van Stock Added",
      "text" : "You have successfully added this Van Stock item to the Job."
    }
  );

  cti.utils.callAction('call-component-function', { "page" : "VanStockCodes","componentInstanceId":"8d742a88-9c0d-4b70-b7bd-1b8ef96568eb","function":"bind"});
  cti.utils.updatePage();

  cti.store.variables.menuOption = "schedules";
  cti.fnSearchPanel(cti.store.variables.menuOption);
} else {
  cti.fnCloseSpinner();
  cti.utils.callAction(
    "show-notification", {
      "name" : "app_sorcodematch",
      "item-type" : "app-notification",
      "style" : "error",
      "title" : "Warning",
      "text" : "You have already added this Van Stock Code to the Job."
    }
  );
}

cti.store.variables.showFullLocations = false;
delete cti.store.variables.vanStockSearchResults;
delete cti.store.variables.vanStockJobSearchResults;
