delete cti.store.variables.menuOption;

cti.utils.callAction('go-to-page', { "name": "VanLocationSelect" });

$( "#vanLocationFetch" ).click(function() {
  cti.utils.callAction('call-actionflow',{'name':'OnlineStockLocations'});
});

cti.fnOrderVanLocations();
$success();

// Load Spinner
cti.fnOpenSpinner('Loading Van Stock...');

cti.store.variables.selectedLocation = {{$data.$item}};

if(cti.store.offlineApi.AccuServOffline.vanstock != undefined) {
  var stock = cti.store.offlineApi.AccuServOffline.vanstock.aOperativeVanStock;
  var vanStock = cti.store.jobs.selectedJob.aVanStock;
  var selectedLocation = cti.store.variables.selectedLocation;
  var exists = false;

  for(var i in vanStock) {
    if(vanStock[i].sLocation == selectedLocation.sLocation) {
      exists = true;
      break;
    }
  }

  if(exists) {
    cti.store.variables.menuOption = "schedules";
  } else {
    if(selectedLocation.sVanStockQuantity != 0) {
      cti.store.variables.menuOption = "schedules";
    } else {
      cti.store.variables.menuOption = "search";
    }
  }

  cti.store.variables.vanStockSearchResults = [];
  var count = 10;

  for(var i in stock) {
    if(count === 0) {
      break;
    } else {
      count--;
      cti.store.variables.vanStockSearchResults.push(stock[i]);
    }
  }

  cti.utils.callAction('go-to-page', { "name": "VanStockCodes" });

  $success();
} else {
  cti.utils.callAction('call-actionflow', { "name": "OnlineVanCodes" });
}

cti.fnCloseSpinner();

// Example of setting up handlers do deal with quantity changes from the numeric-toggler components
function valueChangeHandler(e) {
 	e = e || window.event;
	var targ = e.target || e.srcElement;
    console.log(targ.id + ' has new value = ' + targ.quantity);
}

var togglers = document.getElementsByTagName("numeric-toggler");
for (var i = 0; i < togglers.length; i++) {
  togglers[i].removeEventListener('changed', valueChangeHandler);
  togglers[i].addEventListener('changed', valueChangeHandler, true);
}
$success();

var exceededBand = cti.fnSORAlgorithms(false, false);
cti.store.variables.exceededBand = exceededBand;

if(cti.store.variables.exceededBand) {
  cti.utils.callAction(
    "show-notification", {
      "name" : "app_sorcodematch",
      "item-type" : "app-notification",
      "style" : "error",
      "title" : "Warning",
      "text" : "You have exceeded Band C for the Job Cost Variation Bandings. You are not permitted to add any more SOR codes, or increase the quanitity of your current SOR codes until you are back within Band B."
    }
  );
} else {  
  cti.fnOpenSpinner('Adding SOR Code...');

  var sorCodes = cti.store.jobs.selectedJob.aItemGroups;
  var code = {{$data.$item}};
  var exists = false;

  var codeObj = code;
  codeObj.sLocation = cti.store.variables.selectedLocation.sLocation || "NOLOCATION";
  codeObj.nQtyRequired = 1;
  codeObj.nQtyDoneThisVisit = 0;
  codeObj.nQtyRemaining = 1;
  codeObj.bAccuServCode = false;

  for(var i in sorCodes) {
    if(sorCodes[i].sCode == codeObj.sCode) {
      if(sorCodes[i].sLocation == codeObj.sLocation) {
        exists = true;
        break;
      }
    }
  }

  if(!exists) {
    sorCodes.unshift(codeObj);

    cti.fnCloseSpinner();
    cti.utils.callAction(
      "show-notification", {
        "name" : "app_sorcodeadded",
        "item-type" : "app-notification",
        "style" : "success",
        "title" : "SOR Code Added",
        "text" : "You have successfully added this SOR Code to the Job."
      }
    );

    cti.utils.callAction('call-component-function', { "page" : "SORCodes","componentInstanceId":"506ab6a8-0172-4024-a0e6-ae59ed29f1ee","function":"bind"});
    cti.utils.updatePage();

    cti.store.variables.menuOption = "schedules";
    cti.fnSearchPanel(cti.store.variables.menuOption);
  } else {
    cti.fnCloseSpinner();
    cti.utils.callAction(
      "show-notification", {
        "name" : "app_sorcodematch",
        "item-type" : "app-notification",
        "style" : "error",
        "title" : "Warning",
        "text" : "You have already added this SOR Code to the Job in this Location."
      }
    );
  }
}

$data.$item.nQtyDoneThisVisit = $data.$item.nQtyDoneThisVisit - 1;
$data.$item.nQtyRemaining = $data.$item.nQtyRemaining + 1;

cti.fnSORAlgorithms(false, false);

if($data.$item.nInitialQty === undefined) {
  $data.$item.nInitialQty = $data.$item.nQtyRemaining;
}

$data.$item.nQtyDoneThisVisit = $data.$item.nQtyDoneThisVisit + 1;
if($data.$item.nQtyRemaining > 0) {
  $data.$item.nQtyRemaining = $data.$item.nQtyRemaining - 1;
}

var exceededBand = cti.fnSORAlgorithms(false, false);
cti.store.variables.exceededBand = exceededBand;

if($data.$item.nQtyRemaining > 0 ) {
  delete cti.store.variables.exceededBand;
} else if($data.$item.nQtyDoneThisVisit <= $data.$item.nInitialQty) {
  delete cti.store.variables.exceededBand;
}

if(cti.store.variables.exceededBand) {
  $data.$item.nQtyDoneThisVisit = $data.$item.nQtyDoneThisVisit - 1;
  if($data.$item.nQtyRemaining > 0) {
    $data.$item.nQtyRemaining = $data.$item.nQtyRemaining + 1;
  }

  cti.utils.callAction(
    "show-notification", {
      "style" : "warning",
      "name" : "app_sorcodematch",
      "item-type" : "app-notification",
      "title" : "Warning",
      "text" : "You have exceeded Band C for the Job Cost Variation Bandings. You are not permitted to add any more SOR codes, or increase the quanitity of your current SOR codes until you are back within Band B."
    }
  );
}

$data.$item.nQtyRemaining = $data.$item.nQtyRemaining -1;

// Update the jobcost and ignore any band variation checks
cti.fnSORAlgorithms(false, true);

if($data.$item.nInitialQty === undefined) {
  $data.$item.nInitialQty = $data.$item.nQtyRemaining;
}

$data.$item.nQtyRemaining = $data.$item.nQtyRemaining + 1;

var exceededBand = cti.fnSORAlgorithms(false, false);
cti.store.variables.exceededBand = exceededBand;

var total = $data.$item.nQtyRemaining + $data.$item.nQtyDoneThisVisit;
if(total <=  $data.$item.nInitialQty) {
  delete cti.store.variables.exceededBand;
}

if(cti.store.variables.exceededBand) {
  $data.$item.nQtyRemaining = $data.$item.nQtyRemaining - 1;
  cti.utils.callAction(
    "show-notification", {
      "name" : "app_sorcodematch",
      "item-type" : "app-notification",
      "style" : "warning",
      "title" : "Warning",
      "text" : "You have exceeded Band C for the Job Cost Variation Bandings. You are not permitted to add any more SOR codes, or increase the quanitity of your current SOR codes until you are back within Band B."
