/* Process Vehicle Check Function */
var enteredOdo = cti.store.pages.VehicleChecks.vehicleOdometer;
var lastOdo = cti.store.api.AccuServ.initialise.nlastRecordedMileage || 0;
var enteredDifference = enteredOdo - lastOdo;

if(enteredOdo < lastOdo) {
  cti.utils.callAction(
    "show-notification", {
      "name": "app_odometer",
      "item-type": "app-notification",
      "style": "Warning",
      "title": "Warning!",
      "text": "Input mileage can not be less than the last recorded reading."
    }
  );
} else if(enteredOdo === 0) {
  cti.utils.callAction(
    "show-notification", {
      "name": "app_odometer",
      "item-type": "app-notification",
      "style": "Warning",
      "title": "Warning!",
      "text": "Please ensure you enter your vehicle mileage. It must be more than 0."
    }
  );
} else if(cti.store.staticData.vehicleMileageMaxEntry < enteredDifference) {
  cti.utils.callAction(
    "show-notification", {
      "name": "app_odometer",
      "item-type": "app-notification",
      "style": "Warning",
      "title": "Warning!",
      "text": "Please make sure the difference between the current entry and last entry is less than " + cti.store.staticData.vehicleMileageMaxEntry + " miles."
    }
  );
} else {
  delete cti.store.saveActionPayload;

  cti.utils.callAction('go-to-page', { name: "VehicleChecksPhotos" });
}

