}

/* jobstatus endpoint success callback */

var resp = cti.store.offlineApi.AccuServOffline.jobstatus;
var responseJob = 0;
var code;

if(resp) {
  if(resp.jobID !== undefined) {
    responseJob = resp.jobID.iJobEventNoKey
  }

  if(cti.store.payloads.eventStatusPayload != undefined) {
    code = cti.store.payloads.eventStatusPayload.sStatus;
  } else {
    code = "Returned out of scope.";
  }

  var job = cti.store.jobs.selectedJob;

  if(job != undefined) {
    if(responseJob === job.aAccuServJobID.iJobEventNoKey) {
      cti.store.jobs.eventStatus = {
          sStatus: resp.sStatus,
          sCode: code
      };

      if(resp.bSuccess) {
        cti.store.jobs.selectedJob.sStatus = resp.sStatus;

        if(code == "70" || code == "71" || code == "72" || code == "74") {
          cti.store.variables.reSyncJobList = true;
          cti.store.jobs.lastJobEvent.aEventStatus = cti.store.jobs.eventStatus;
        }
      }
    } else {
      window.appManager.logEvent('Job Status Response returned outside scope of current job.' , 'info');
    }
  } else {
    window.appManager.logEvent('Job Status Response returned outside scope of any job.' , 'info');
  }
} else {
  window.appManager.logEvent('Job Status Response returned no response.' , 'error');
}

delete cti.store.variables.sendEventStatusHeaders;
