// Encode any jpegs to base64
function toDataURL(thisPhoto, callback) {
  var xhr = new XMLHttpRequest();
  xhr.onload = function() {
    var reader = new FileReader();
    reader.onloadend = function() {
      callback(reader.result, thisPhoto);
    };
    reader.readAsDataURL(xhr.response);
  };
  xhr.open("GET", thisPhoto.src);
  xhr.responseType = "blob";
  xhr.send();
}

// Check that the photos element is undefined and define if needed
if (cti.store.jobs.failedPhotosToSend.photos === undefined) {
  cti.store.jobs.failedPhotosToSend.photos = [];
}

// Check that the signatures element is undefined and define if needed
if (cti.store.jobs.failedPhotosToSend.signatures === undefined) {
  cti.store.jobs.failedPhotosToSend.signatures = [];
}

// Check that we have any photos or signatures
if (
  cti.store.jobs.failedPhotosToSend.photos.length > 0 ||
  cti.store.jobs.failedPhotosToSend.signatures.length > 0
) {
  var photoLength = cti.store.jobs.failedPhotosToSend.photos.length;

  // If we have any photos then kick off the endcoding proccess ...
  if (photoLength > 0) {
    for (var i in cti.store.jobs.failedPhotosToSend.photos) {
      var thisPhoto = cti.store.jobs.failedPhotosToSend.photos[i];

      toDataURL(thisPhoto, function(dataUrl, thisPhoto) {
        thisPhoto.content = dataUrl;
        photoLength--;

        if (photoLength === 0) {
          $success();
        }
      });
    }
  } else {
    // ... otherwise bypass this and submit the signautres only
    $success();
  }
} else {
  // No photos or signatures so just process the jobcomplete event
  cti.fnClearPhotos(cti.store.jobs.failedPhotosToSend.photos, true);
  cti.utils.callAction("call-actionflow", { name: "JobComplete" });
}

// fnUploadPhotos function callback to handle the response of the image/signature upload
function uploadedCallbackEvent(ok) {
  if (ok) {
    for (var i in cti.store.jobs.failedPhotos) {
      var thisPhoto = cti.store.jobs.failedPhotos[i];
      if (
        cti.store.jobs.failedPhotosToSend.job.aAccuServJobID.iJobEventNoKey ===
        thisPhoto.job.aAccuServJobID.iJobEventNoKey
      ) {
        cti.store.jobs.failedPhotos.splice(i, 1);
      }
    }

    cti.fnClearPhotos(cti.store.jobs.failedPhotosToSend.photos, false);
    window.appManager.logEvent(
      "Photo/Signature re-submitted successfully from Job ID: " +
        cti.store.jobs.failedPhotosToSend.job.aAccuServJobID.sJobNo,
      "info"
    );

    delete cti.store.jobs.failedPhotosToSend;
    delete cti.store.variables.signatures;
    delete cti.store.jobs.signaturesAssociatedJob;
  } else {
    for (var i in cti.store.jobs.failedPhotosToSend.photos) {
      delete cti.store.jobs.failedPhotosToSend.photos[i].content;
    }

    window.appManager.logEvent(
      "Photo/Signature failed to send for Job ID: " +
        cti.store.jobs.failedPhotosToSend.job.aAccuServJobID.sJobNo +
        ". Re-submitting...",
      "error"
    );
  }
}

cti.fnUploadPhotos(
  cti.store.jobs.failedPhotosToSend,
  cti.store.pages.AppSettings.accuservAPIAddress,
  uploadedCallbackEvent,
  false
);

// Get jobEventStatus
var jobEventStatus = cti.store.variables.jobEventStatus;

if (jobEventStatus === "jobcomplete") {
  cti.fnOpenSpinner("Preparing Job Complete to send to AccuServ...");
} else if (jobEventStatus === "abandonjob") {
  cti.fnOpenSpinner("Preparing to Abandon Job to send to AccuServ...");
} else if (jobEventStatus === "noaccess") {
  cti.fnOpenSpinner("Preparing to send No Access for Job to AccuServ...");
}

// Encode any jpegs to base64
function toDataURL(thisPhoto, callback) {
  var xhr = new XMLHttpRequest();
  xhr.onload = function() {
    var reader = new FileReader();
    reader.onloadend = function() {
      callback(reader.result, thisPhoto);
    };
    reader.readAsDataURL(xhr.response);
  };
  xhr.open("GET", thisPhoto.src);
  xhr.responseType = "blob";
  xhr.send();
}

var opSig = false;
var riskSig = false;
var cusSig = false;

// Set booleans to indicate any signatures present
if (
  cti.store.pages.Completion !== undefined &&
  cti.store.pages.Completion.operativeSignature !== undefined
) {
  opSig = true;
}
if (
  cti.store.pages.RiskAssessment !== undefined &&
  cti.store.pages.RiskAssessment.confirmRASignature !== undefined
) {
  riskSig = true;
}
if (
  cti.store.pages.CustomerSignature !== undefined &&
  cti.store.pages.CustomerSignature.customerSignature !== undefined
) {
  cusSig = true;
}

if (cti.store.jobs.selectedJob.photos === undefined) {
  cti.store.jobs.selectedJob.photos = [];
}

// Check if we have any signatures or photos
if (
  cti.store.jobs.selectedJob.photos.length > 0 ||
  opSig ||
  riskSig ||
  cusSig
) {
  var photoLength = cti.store.jobs.selectedJob.photos.length;

  // If we have any photos then kick off the endcoding proccess ...
  if (photoLength > 0) {
    for (var i in cti.store.jobs.selectedJob.photos) {
      var thisPhoto = cti.store.jobs.selectedJob.photos[i];

      toDataURL(thisPhoto, function(dataUrl, thisPhoto) {
        thisPhoto.content = dataUrl;
        photoLength--;

        if (photoLength === 0) {
          $success();
        }
      });
    }
  } else {
    // ... otherwise bypass this and submit the signautres only
    $success();
  }
} else {
  // No photos or signatures so just process the jobcomplete event
  cti.fnClearPhotos(cti.store.jobs.selectedJob.photos, true);
  cti.utils.callAction("call-actionflow", { name: "JobComplete" });
}

// fnUploadPhotos function callback to handle the response of the image/signature upload
function uploadedCallbackEvent(ok) {
  if (ok) {
    delete cti.store.variables.signatures;
    delete cti.store.jobs.signaturesAssociatedJob;

    cti.fnClearPhotos(cti.store.jobs.selectedJob.photos, true);
    cti.utils.callAction("call-actionflow", { name: "JobComplete" });
  } else {
    for (var i in cti.store.jobs.selectedJob.photos) {
      delete cti.store.jobs.selectedJob.photos[i].content;
    }

    cti.store.payloads.failedPhotos = {
      photos: cti.store.jobs.selectedJob.photos,
      job: cti.store.jobs.selectedJob,
      signatures: cti.store.variables.signatures
    };

    cti.fnStashPhotos();
    delete cti.store.variables.signatures;
  }
}

cti.fnOpenSpinner("Uploading photos and signatures to AccuServ...");
cti.fnUploadPhotos(
  cti.store.jobs.selectedJob.photos,
  cti.store.pages.AppSettings.accuservAPIAddress,
  uploadedCallbackEvent,
  true
);

// Fetch the jobCompleteOfflineHeaders to supply to the jobcompletion request
cti.store.variables.jobCompleteOfflineHeaders = window.appManager.newHeaderData();
cti.store.payloads.jobCompleteOffline = undefined;

var jobDetail = cti.store.jobs.failedJobToSend;
cti.store.payloads.jobCompleteOffline = {};

if(jobDetail.aItemGroups == undefined) jobDetail.aItemGroups = [];
if(jobDetail.aVanStock == undefined) jobDetail.aVanStock = [];

for(var i in jobDetail.aItemGroups) { 
  delete jobDetail.aItemGroups[i].$index;
}

for(var i in jobDetail.aVanStock) { 
  delete jobDetail.aVanStock[i].$index;
}

var d = new Date();
var finishHours = (d.getHours()<10?'0':'') + d.getHours();
var finishMins = (d.getMinutes()<10?'0':'') + d.getMinutes();

var obj = {
  "bSuccess" : jobDetail.bSuccess,
  "sStatus" : jobDetail.sStatus,
  "sResultSummary" : jobDetail.sResultSummary,
  "sResultSummarySubReason" : jobDetail.sResultSummarySubReason,
  "sResultDetail" : jobDetail.sResultDetail,
  // ML Travel Date/Time Code
  "dStartTravelDate" : jobDetail.dtJobTravelStartedDateTime,
  "iStartTravelHrs" : parseInt(jobDetail.iJobStartedTravelHrs),
  "iStartTravelMins" : parseInt(jobDetail.iJobStartedTravelMins),
  //
  "dtArrivedOnDateTime" : jobDetail.dtJobTravelEndedDateTime,
  "dArrivedOnDate" : jobDetail.dtJobTravelEndedDateTime,
  "iArrivedOnHrs" : parseInt(jobDetail.iJobEndedTravelHrs),
  "iArrivedOnMins" : parseInt(jobDetail.iJobEndedTravelMins),
  "dtFinishedOnDateTime" : moment().format(),
  "dFinishedOnDate" : moment().format(),
  "iFinishedOnHrs" : parseInt(finishHours),
