  var detailObj = JSON.parse(JSON.stringify(resp));
  var items = [];

  for(var i in detailObj.aItemGroups) {
    var thisItem = detailObj.aItemGroups[i]; 
    var str = JSON.stringify(thisItem);

    str = str.replace(/sLongDescription/g, 'sLongDescr');
    str = str.replace(/sDescription/g, 'sShortDescr');

    thisItem = JSON.parse(str);

    delete thisItem.nQtyDoneToDate;
    delete thisItem.sID;
    delete thisItem.sPriceBook;
    delete thisItem.sSpecialInstruction;
    delete thisItem.sUnitOfMeasure;

    if(thisItem.nSMVs == "" || thisItem.nSMVs == undefined) {
      thisItem.nSMVs = 0;
    }
    if(thisItem.nSellPrice == "" || thisItem.nSellPrice == undefined) {
      thisItem.nSellPrice = 0;
    }
    thisItem.sAttrib1 = "";
    thisItem.sAttrib2 = "";
    thisItem.sAttrib3 = "";
    thisItem.sAttrib4 = "";
    thisItem.sStockUnit = "";
    thisItem.sTrade = "";
    thisItem.nQtyRemaining = thisItem.nQtyRequired;

    if(thisItem.sLongDescr == "" || thisItem.sLongDescr == undefined) {
      thisItem.sLongDescr = thisItem.sShortDescr;
    }

    items.push(thisItem);
  }

  detailObj.aItemGroups = items;

  if(detailIndex == undefined) {
    jobDetails.push(detailObj);
  } else {
    jobDetails[detailIndex] = detailObj;
  }

  // Re-set the jobDetail store
  cti.store.jobs.jobDetails = jobDetails;

  // Append selectedJob object
