// Sort function
function compare(a,b) {
  if (a.iDisplayOrder > b.iDisplayOrder) return 1;
  if (a.iDisplayOrder < b.iDisplayOrder) return -1;
  return 0;
}

// On the fly HTTP request to get the lat/long from address supplied
function httpGetLatLong(theUrl, callback) {
  var geocodeRequest = new XMLHttpRequest();
  geocodeRequest.onreadystatechange = function() { 
    if (geocodeRequest.readyState == 4 && geocodeRequest.status == 200)
      callback(geocodeRequest.responseText);
  }
  geocodeRequest.open("GET", theUrl, true);
  geocodeRequest.send(null);
}

// Fetchs the array index of a job from the jobList
function findSummaryEntry(jobid, jobList) {
  for (var k in jobList) {
    var thisJob = jobList[k];
    if (thisJob.aAccuServJobID.iJobEventNoKey == jobid) {
      return k;
    }
  }
  return undefined;
}

