  cti.store.jobs.jobListShort = [];

  var jobListShort = cti.store.jobs.jobListShort;
  var jobListOnDevice = cti.store.jobs.jobList;

  jobListResponse.sort(compare);

  for(var i in jobListOnDevice) {
    var thisJob = jobListOnDevice[i];
    var geocodeApi = "https://maps.googleapis.com/maps/api/geocode/json?address=" + thisJob.sPostcode + "&key=AIzaSyD7SEAfX-pd_mRgCJW6LHYWBlmwVMBVBF0";
    var date = moment(jobListOnDevice[i].dAttendOnDate);
    var index;

    thisJob.sDay = date.format('Do');
    thisJob.sMonth = date.format('MMM');

    httpGetLatLong(geocodeApi, function(response) {
      // On success response push results into a store array
      var resp = JSON.parse(response);
      var latlong = resp.results[0].geometry.location;

      cti.store.jobs.mapLocations.push(latlong);
    });

    if(i == "0" || i == "1") {
      jobListShort.push(thisJob);
    }

    if(thisJob.sProblemDetail.length >= 50) {
      thisJob.sProblemDetailDisplay = thisJob.sProblemDetail.substring(0,50) + "...";
    } else {
      thisJob.sProblemDetailDisplay = thisJob.sProblemDetail;
    }
  }

  for (var k in currentJobDetails) {
    var thisJob = currentJobDetails[k];
    var summaryIndex = findSummaryEntry(thisJob.aAccuServJobID.iJobEventNoKey, jobListOnDevice);
    if (summaryIndex === undefined) {
      currentJobDetails.splice(k, 1);
    }
  }

  if(cti.store.variables.reSyncJobListAuto) {
    cti.store.variables.reSyncJobListAuto = undefined;
  } else {
    cti.store.variables.reSyncJobList = false;
    // Navigate to the Job List page
    cti.utils.callAction('go-to-page', { "name": "JobList" });
  }

  // Check to see if we have any jobs, and if so call the GetJobDetailOffline workflow
  if(jobListOnDevice.length > 0) {
    cti.store.variables.showFullJobs = false;
    cti.store.jobs.retrieveJobRetry = 10;
  } else {
    cti.store.variables.showFullJobs = undefined;
  } 
} else {
  cti.store.variables.reSyncJobListAuto = undefined;
  // Notify operative that the request failed 
  cti.utils.callAction(
    "show-notification", {
      "name" : "api_response_error",
      "item-type" : "app-notification",
      "style" : "error",
      "title" : "Error Retrieving Job List",
      "text" : resp.sStatus || "A problem occured when trying to retrieve the Job List"
    }
  );
  cti.store.variables.showFullJobs = undefined;
}

