/* Login API Request Package Function */
var settings = true;

if(cti.store.pages.AppSettings === undefined || cti.store.pages.AppSettings.accuservAPIAddress === undefined || cti.store.pages.AppSettings.accuservAPIAddress == '') {
  if(cti.store.schema.metadata.accuservurl === undefined) {
