      }
    );

    settings = false;
  } else {
    if(cti.store.pages.AppSettings == undefined) {
      cti.store.pages.AppSettings = {};
    }

    cti.store.pages.AppSettings.accuservAPIAddress = cti.store.schema.metadata.accuservurl;
  }
}

if(settings) {
  // Load Spinner
  cti.fnOpenSpinner('Attempting to log in...');

  // Fetch the loginHeaders to supply to the login request
  cti.store.variables.loginHeaders = window.appManager.newHeaderData();

  // Check and defined the payloads store
  if(cti.store.payloads == undefined) cti.store.payloads = {};

  // Create a login payload of the username and password to send to the login request
  cti.store.payloads.login = {
    "username": cti.store.pages.Login.Username,
    "password": cti.store.pages.Login.Password
  };

  $success();
}

/* Login API Success Response Function */

// Create link to API for Google Maps
var google = document.getElementById('google');

// Check if we haven't already added the script tag
if(google == undefined) {
  window.appManager.logEvent('Google Maps API not loaded,  initialising...', 'info');
  var googleMaps = document.createElement('script');
  googleMaps.id = 'google';
  googleMaps.src = 'https://maps.googleapis.com/maps/api/js?key=' + cti.store.schema.metadata.googleapikey;
  document.body.appendChild(googleMaps);
  window.appManager.logEvent('Google Maps API initialised successfully.', 'info');
} else {
  window.appManager.logEvent('Google Maps API initialised', 'info');
}

// Hide variable set to hide elements not wanted yet in this version: Example vehicle reg
cti.store.hide = false;

// Just encoded at the mo, but we'll ultimately use an encrypted hash for an offline login check later. We'll only store this if the login succeeds
var username = cti.store.pages.Login.Username.toLowerCase();
var credentialHash = btoa(username + '==' + cti.store.pages.Login.Password);
var dt = new Date();

// Make certain the password is cleared
delete cti.store.pages.Login.Password;

// Grab the result so we can check if our login request was actually accepted
// We'll put it in a holding object so we can delete the API store response
var loginResult = {
  "errorCode": cti.store.api.AccuServ.login.errorCode,
  "errorMessage": cti.store.api.AccuServ.login.errorMessage,
  "token": cti.store.api.AccuServ.login.token,
  "startOfDayRequired": cti.store.api.AccuServ.login.startOfDayRequired,
  "expiresIn": cti.store.api.AccuServ.login.expiresIn,
  "jobsRequired": cti.store.api.AccuServ.login.jobsRequired,
  "messageid": cti.store.api.AccuServ.login.messageid,
  "maxLoginDuration": cti.store.api.AccuServ.login.maxLoginDuration || (60 * 14)
}

// Delete API store response for Login
delete cti.store.api.AccuServ.login;

// Check response for failure 
//
// Proceed through workflow to Initialise if errorCode returns 0 
// and startOfDayRequired == true
// Proceed straight to JobList otherwise
if(loginResult.errorCode == 0) {
  window.accuServFunctions.fnInitialiseModel();
  cti.store.variables.successLogin = true;

  // Is the user the same as the last logged in user?
  if(cti.store.lastLogin !== undefined) {
    // Different user - van stock is redundant
    if(username != cti.store.lastLogin.username) {
      window.appManager.logEvent('Different user logging in: ' + username, 'info');
      delete cti.store.offlineApi.AccuServOffline.vanstock;
      delete cti.store.staticData.vanStockLastRetrievedDate;
    }
    else if (window.appManager.areDatesEqual(cti.store.lastLogin.dateTime, dt)) {
      window.appManager.logEvent('Logging back in: ' + username, 'info');
      var previousUser = true;
    }
    else {
      window.appManager.logEvent('First login today: ' + username, 'info');
    }
  }
  else  {
    window.appManager.logEvent('New user logging in: ' + username, 'info');
  }

  // Store the user as the last user to login
  cti.store.lastLogin = {
    "userHash": credentialHash,
    "username": username,
    "dateTime": dt
  };

  // PW I've pre-emptively supported the ability for the login method to tell us the maximum length of our
  // session, during which we may keep re-acquiring a token to keep the session running. Once we reach this
  // threshhold (maxLoginDuration; defaults to 14 hours from login), the user must fully login again.
  var maxLoginDurationExpiry = new Date();
  maxLoginDurationExpiry.setMinutes(maxLoginDurationExpiry.getMinutes() + loginResult.maxLoginDuration);

  // Create a current profile for the user
  cti.store.currentProfile = {
    "username": username,
    "token": loginResult.token,
    "dateTime": dt,
    "maxLoginDurationExpiry": maxLoginDurationExpiry,
    "jobsRequired": loginResult.jobsRequired
  };

  window.accuServFunctions.fnUpdateAuthToken(loginResult.token, loginResult.expiresIn);

  if(loginResult.startOfDayRequired) {
    window.accuServFunctions.fnDataDestroy();
    cti.utils.callAction('call-actionflow', { "name": "Initialise" });
  } else {
    if(cti.store.staticData.updateStatus === undefined) {
      cti.store.staticData.updateStatus = {
        "updateSORCodes": true,
        "updateVanStock": true,
        "updateSupplier": true,
        "updateJobLocations": true,
        "updateCatalogue": true,
        "updateSummaries" : true,
        "updateTrades" : true
      }
    } else {
      cti.store.staticData.updateStatus.updateSummaries = true;
      cti.store.staticData.updateStatus.updateTrades = true;
    } 

    if(previousUser) {
      if(cti.store.variables.timedOut) {
        cti.store.variables.timedOut = false;
        cti.utils.callAction('go-to-page', { "name": cti.store.variables.lastPage });
      } else {
        cti.fnCloseSpinner();
        cti.utils.callAction('call-actionflow', { "name": "StaticData" });
        cti.utils.callAction('call-actionflow', { "name": "GetJobList" });
      }
    } else {
      cti.fnCloseSpinner();
      cti.utils.callAction('call-actionflow', { "name": "StaticData" });
      cti.utils.callAction('call-actionflow', { "name": "GetJobList" });
    }
  }
} else {
  // We have failed to login, display returned error message
  cti.utils.callAction(
    "show-notification", {
      "name": "api_response_error",
      "title": "Login Failed",
      "text": loginResult.errorMessage || "Please provide a valid Username and Password"
    }
  );
}

// Delete loginHeaders payload object
delete cti.store.variables.loginHeaders;

// Close Spinner
cti.fnCloseSpinner();

/* Login API Fail Response Function */

// Make certain the password is cleared
delete cti.store.pages.Login.Password;

// TODO Depending on the nature of the failure, we can allow the user to log in using cached credentials

// Delete loginHeaders payload object
delete cti.store.variables.loginHeaders;

// Notify operative that the request failed 
cti.utils.callAction(
  "show-notification", {
    "name": "api_failure_error",
    "title": "Login failed",
    "text": "We couldn't reach the AccuServ server to log you in. Please contact support"
  }
);

window.appManager.logEvent('Failed to process login request (API connection).', 'error');

// Close Spinner
cti.fnCloseSpinner();

