debugger;
function initMap(positions) {
  var currentLocation = {lat: positions[0].lat, lng: positions[0].lng};

  var map = new google.maps.Map(document.getElementById('map'), {
    zoom: 8,
    center: currentLocation
  });

  var infoWindow = new google.maps.InfoWindow({map: map});

  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(function(position) {
      var pos = {
        lat: position.coords.latitude,
        lng: position.coords.longitude
      };

      infoWindow.setPosition(pos);
      infoWindow.setContent('Location found.');
      map.setCenter(pos);
    }, function() {
      handleLocationError(true, infoWindow, map.getCenter());
    });
  } else {
    // Browser doesn't support Geolocation
    handleLocationError(false, infoWindow, map.getCenter());
  }

  var marker = new google.maps.Marker({
    icon: 'http://maps.google.com/mapfiles/ms/icons/green-dot.png',
    position: currentLocation,
    map: map
  });

  for(var i in positions) {
    var marker = new google.maps.Marker({
      position: positions[i],
      map: map
    });
  }
}

function handleLocationError(browserHasGeolocation, infoWindow, pos) {
  infoWindow.setPosition(pos);
  infoWindow.setContent(browserHasGeolocation ?
                        'Error: The Geolocation service failed.' :
                        'Error: Your browser doesn\'t support geolocation.');
}

var mapLocations = cti.store.jobs.mapLocations;

if(mapLocations.length > 0) {
  initMap(mapLocations);
}

cti.utils.updatePage();

cti.fnCloseSpinner();

config.content:
if(online) {
  var photos = photosObject || cti.store.jobs.selectedJob.photos || [];
  cti.store.jobs.photoAssociatedJobPhotos = photos;
} else {
  var photos = photosObject || cti.store.jobs.failedPhotosToSend.photos || [];
  cti.store.jobs.photoAssociatedJobPhotos = photos;
}

var l = photos.length;
var instance = this;
var thisPhoto = undefined;
var failCount = 0;

if (l == 0) { 
  completeEvent(); 
  return true; 
}

//window.appManager.logEvent('Deleting photos', 'info');

if (!window.cordova) {
  for (var i = l; i>=0; i--) {
    photos.splice(i, 1);
  }
  completeEvent(); 
  return true; 
}

// Start at the last photo and work our way back
var photoIndex = l;

function completeEvent() {
  delete cti.store.jobs.photoAssociatedJobPhotos;

  if (failCount > 0) { 
    if (typeof this.$fail === "function") { this.$fail(); } else { return false; }
  } else { 
    if (typeof this.$success === "function") { this.$success(); } else { return true; }
  }
}

function processNextPhoto() {
  var requireDelete = false;
  photoIndex--;
  if (photoIndex >= 0) {
    var startIndex = photoIndex;
    for (var i = startIndex; i >= 0; i--) {
      photoIndex = i;
      // TODO Only delete if uploaded??
      requireDelete = true;
      break;
    }
  }
  if (requireDelete) {
    window.resolveLocalFileSystemURI(cti.store.jobs.photoAssociatedJobPhotos[photoIndex].src, function(entry) {
      entry.remove(function(){
        photos.splice(photoIndex, 1);
        processNextPhoto();
      },function(e1){
        failCount++;
        window.appManager.logError('Unable to remove file when deleting photo', e1);
        processNextPhoto();
      },function(){
        window.appManager.logError('Unable to remove file when deleting photo');
        photos.splice(photoIndex, 1);
        processNextPhoto();
      });
    }, function(e2) {
      failCount++;
      window.appManager.logError('Unable to resolve file URL when deleting photo', e2);
      // See if we can delete others
      processNextPhoto();
    });            
  }
  else {
    // No more photos to be deleted, fire the completion event
    completeEvent();
  }
}

window.requestFileSystem(LocalFileSystem.PERSISTENT, 0, function(fileSys) { 
  instance.fileSys = fileSys;
  // Start the process once we know we have the file system etc
  processNextPhoto();
}, function(e) {
  failCount++;
  //window.appManager.logError('Unable to retrieve file system for deleting photos', e);
  completeEvent();
});

// Delete the currently selected photo - ie the one we are viewing in the PhotoDetail page
// Find it's location in the source array
var index = undefined;
for (var k in cti.store.variables.selectedPhotoList) {
  if (cti.store.variables.selectedPhotoList[k].id == cti.store.variables.selectedPhoto.id) {
    index = k;
    break;
  }
}

if (index === undefined) {
  //console.log("debug:: Photo id " + id + " not found for deletion");
  return false;
}

if(!window.cordova) {
  cti.fnCloseSpinner();
  cti.store.variables.selectedPhotoList.splice(index, 1);
  delete cti.store.variables.selectedPhoto;

  cti.store.variables.fromPhotoDetail = true;
  cti.utils.updatePage();

  var lastPage = JSON.parse(JSON.stringify(cti.store.variables.lastPhotoDetailPage));
  cti.utils.callAction("go-to-page", { "name" : lastPage });
  delete cti.store.variables.lastPhotoDetailPage;
} else {      
  window.requestFileSystem(LocalFileSystem.PERSISTENT, 0, function(fileSys) { 
    window.resolveLocalFileSystemURL(cti.store.variables.selectedPhotoList[index].src, function(entry) {
      entry.remove(function(){
        cti.fnCloseSpinner();
        cti.store.variables.selectedPhotoList.splice(index, 1);
        delete cti.store.variables.selectedPhoto;

        cti.store.variables.fromPhotoDetail = true;
        cti.utils.updatePage();

        var lastPage = JSON.parse(JSON.stringify(cti.store.variables.lastPhotoDetailPage));
        cti.utils.callAction("go-to-page", { "name" : lastPage });
        delete cti.store.variables.lastPhotoDetailPage;
      },function(e1){
        cti.fnCloseSpinner();
        window.appManager.logEvent("error:: Problem removing file - " + JSON.stringify(e1), 'error');
        cti.utils.callAction(
          "show-notification", {
            "name": "api_failure_error",
            "title": "Delete failed",
            "text": "Problem removing file - " + JSON.stringify(e1)
          }
        );
      },function(){
        cti.fnCloseSpinner();
        window.appManager.logEvent("error:: Problem removing file - probably does not exist", 'error');
        cti.utils.callAction(
          "show-notification", {
            "name": "api_failure_error",
            "title": "Delete failed",
            "text": "Problem removing file - file does not exist."
          }
        );
      });
    }, function(e2) {
      cti.fnCloseSpinner();
      window.appManager.logEvent("error:: Problem resolving file URL - " + JSON.stringify(e2), 'error');
      cti.utils.callAction(
        "show-notification", {
          "name": "api_failure_error",
          "title": "Delete failed",
          "text": "Problem resolving file URL - " + JSON.stringify(e2)
        }
      );
    });
  }, function(e3) {
    cti.fnCloseSpinner();
    window.appManager.logEvent("error:: Problem getting file system - " + JSON.stringify(e3), 'error');
    cti.utils.callAction(
      "show-notification", {
        "name": "api_failure_error",
        "title": "Delete failed",
        "text": "Problem getting file system - " + JSON.stringify(e3)
      }
    );
  });
}

// fnUploadPhotos v 0.3.0.120

/*

Signatures upload

Locations of signatures:

// Operator sign off for job completion
cti.store.pages.Completion.operativeSignature

// Customer signature
cti.store.pages.CustomerSignature.customerSignature

// RA Sign-off
cti.store.pages.RiskAssessment.confirmRASignature
*/

if(online) {
  var photos = photosObject || cti.store.jobs.selectedJob.photos || [];
  cti.store.jobs.photoAssociatedJob = cti.store.jobs.selectedJob;
  cti.store.jobs.signaturesAssociatedJob = undefined;
} else {
  var photos = photosObject.photos || cti.store.jobs.failedPhotosToSend.photos || [];
  cti.store.jobs.photoAssociatedJob = cti.store.jobs.failedPhotosToSend.job;
  cti.store.jobs.signaturesAssociatedJob = cti.store.jobs.failedPhotosToSend.signatures;
}

var thisPhoto = undefined;
var photosToBeUploaded = 0;
var failCount = 0;
var successCount = 0;

var signatures = [];
var thisSignature = undefined;
var signatureFailCount = 0;
var signatureSuccessCount = 0;

var instance = this;
var currentItemIndex = -1;

function allCompleteEvent() {
  delete cti.store.jobs.photoAssociatedJob;

  // If any uploads fail, fire the fail event, else fire the success event
  if (failCount > 0 || signatureFailCount > 0) { 
    if (callbackEvent !== undefined) { callbackEvent(false); } else if (typeof this.$fail === "function") { this.$fail(); } else { return false; }
  } else { 
    if (callbackEvent !== undefined) { callbackEvent(true); } else if (typeof this.$success === "function") { this.$success(); } else { return true; }
  }
}
function photosCompleteEvent() {
  // Photos have completed - do the signatures
  // Reset the index
  currentItemIndex = -1;
  // Assign what signatures we have. Object needs to look like this:
  // { "content": "--base64 image--", "category": "Signature", "description": "Type of signature" }

  if(cti.store.jobs.signaturesAssociatedJob) {
    for(var i in cti.store.jobs.signaturesAssociatedJob) {
      signatures.push(cti.store.jobs.signaturesAssociatedJob[i]);
    }
  } else {
    if (cti.store.pages.Completion !== undefined && cti.store.pages.Completion.operativeSignature !== undefined) {
      signatures.push({ 
        "docType": "Signature", 
        "docNote": cti.store.pages.Completion.operativeComments || "", 
        "description":"", 
        "docSubType": "Operative job completion", 
        "content": cti.store.pages.Completion.operativeSignature 
      });
    }
    if (cti.store.pages.RiskAssessment !== undefined && cti.store.pages.RiskAssessment.confirmRASignature !== undefined) {
      signatures.push({ 
        "docType": "Signature", 
