function signatureResultCallback(success) {
  if (success) { 
    signatureSuccessCount++; 
    thisSignature.uploaded = true; 
  } else { 
    signatureFailCount++; 
  }

  //window.appManager.logEvent('Upload: ' + success, 'info');
  processNextSignature();
}

function processNextSignature() {
  var requireUpload = false;
  currentItemIndex++;
  if (currentItemIndex < signatures.length) {
    var startIndex = currentItemIndex;
    for (var i = startIndex; i < signatures.length; i++) {
      currentItemIndex = i;
      thisSignature = signatures[currentItemIndex];
      // If the photo needs uploading, attempt to do so
      if (!thisSignature.uploaded) {
        requireUpload = true;
        break;
      }
    }
  }
  if (requireUpload) {
    // function (serverBaseUri, uploadEndpoint, associateEndpoint, sourceObject, jobObject, callback)
    //window.appManager.logEvent('Found signature to upload at index ' + currentItemIndex, 'info');
    window.appManager.uploadBase64AndAssociateWithJob(
      baseUrl, 
      "PostImage", 
      "AttachImageToJobEvent", 
      thisSignature,
      cti.store.jobs.photoAssociatedJob,
      "signature",
      signatureResultCallback
    );
  }
  else {
    // No more signatures to be uploaded, fire the completion event
    allCompleteEvent();
  }
}


function photoResultCallback(success) {
  if (success) { 
    successCount++; 
    thisPhoto.uploaded = true; 
  } else { 
    failCount++; 
  }

  //window.appManager.logEvent('Upload: ' + success, 'info');
  processNextPhoto();
}

function processNextPhoto() {
  var requireUpload = false;
  currentItemIndex++;
  if (currentItemIndex < photos.length) {
    var startIndex = currentItemIndex;
    for (var i = startIndex; i < photos.length; i++) {
      currentItemIndex = i;
      thisPhoto = photos[currentItemIndex];
      // If the photo needs uploading, attempt to do so
      if (!thisPhoto.uploaded) {
        requireUpload = true;
        break;
      }
    }
  }
  if (requireUpload) {
    photosToBeUploaded++;
    //window.appManager.logEvent('Found photo to upload at index ' + currentItemIndex, 'info');

    window.appManager.uploadBase64AndAssociateWithJob(
      baseUrl, 
      "PostImage", 
      "AttachImageToJobEvent", 
      thisPhoto,
      cti.store.jobs.photoAssociatedJob,
      "photo",
      photoResultCallback
    );
    //window.appManager.uploadFile(serverUri, thisPhoto.src, resultCallback);
    //window.appManager.uploadFileAsBase64(serverUri, thisPhoto.src, resultCallback);
  }
  else {
    // No more photos to be uploaded, fire the completion event
    photosCompleteEvent();
  }
}

