var schema = cti.store.schema;

function getObjects(obj, key, val) {
    var objects = [];
    for (var i in obj) {
          if (!obj.hasOwnProperty(i))
              continue;
        if (typeof obj[i] === 'object') {
            objects = objects.concat(getObjects(obj[i], key, val));
        }
        else {
            if (i == key && obj[i] == val || i == key && val == '') {
                objects.push(obj);
            }
            else if (obj[i] == val && key == '') {
                if (objects.lastIndexOf(obj) == -1) {
                    objects.push(obj);
                }
            }
        }
    }
    return objects;
}

function sanitiseAnswer(answer) {
    if (answer === undefined || answer == '') { return undefined; }
    if (typeof answer === 'object' && answer.hasOwnProperty('value')) {
      return answer.value;
    }
      return answer;
}

for (var i = 0; i < schema.pages.length; i++) {
  if (schema.pages[i].name.toLowerCase() == pageName.toLowerCase()) {    
    var userInput = getObjects(schema.pages[i].content, 'type', 'user-input');
    var choice = getObjects(schema.pages[i].content, 'type', 'choice');

    var questions = userInput.concat(choice);

    for (var p in questions) {
      var questionName = questions[p].name;
      var answer = sanitiseAnswer(cti.store.pages[pageName][questionName]);
      if (answer !== undefined) {
        payloadObject[questionName] = answer;
      }
    }
  }
}

