var locations = cti.store.staticData.jobLocations;
var listOfLocations = [];

if(cti.store.variables.showFullLocations === undefined) {
  cti.store.variables.showFullLocations = false;
}

if(locations == undefined) locations = [];

if(locations.length != 0) {
  cti.store.variables.jobLocations = true;

  var orderIdIncrementer = 7;
  var sorLocations = JSON.parse(JSON.stringify(locations));
  var sorCodesOnJob = cti.store.jobs.selectedJob.aItemGroups;

  for(var i in sorLocations) {
    var requiredInLocation = false;
    var sorCodesQuantity = 0;
    var currentLocation = sorLocations[i];

    if(currentLocation.sSORCodesQuantity == undefined) currentLocation.sSORCodesQuantity = {};

    for(var k in sorCodesOnJob) {
      if(sorCodesOnJob[k].sLocation == "") {
        sorCodesOnJob[k].sLocation = "NOLOCATION";
      }

      if(currentLocation.sLocation == sorCodesOnJob[k].sLocation) {
        sorCodesQuantity++;

        if(sorCodesOnJob[k].nQtyRemaining != 0) {
          requiredInLocation = true;
        }
      }
    }

    currentLocation.nSORQuantity = sorCodesQuantity;

    if(sorCodesQuantity > 0) {
      if(requiredInLocation) {
        currentLocation.sLocationClass = "fa required-sorcode";
      } else {
        currentLocation.sLocationClass = "fa added-sorcode";
      }

      currentLocation.sSORCodesQuantity = '(' + sorCodesQuantity + ')';
    } else {
      currentLocation.sSORCodesQuantity = "";
      currentLocation.sLocationClass = "";
    }

    var location = currentLocation.sLocation.toLowerCase();

    if(location.indexOf("kitchen") != -1) {
      currentLocation.nOrderId = 1;
      listOfLocations.push(currentLocation);
    } else if(location.indexOf("bathroom") != -1) {
      currentLocation.nOrderId = 2;
      listOfLocations.push(currentLocation);
    } else if(location.indexOf("lounge") != -1 || location.indexOf("living") != -1) {
      currentLocation.nOrderId = 3;
      listOfLocations.push(currentLocation);
    } else if(location.indexOf("bed") != -1 && location.indexOf("1") != -1) {
      currentLocation.nOrderId = 4;
      listOfLocations.push(currentLocation);
    } else if(location.indexOf("bed") != -1 && location.indexOf("2") != -1) {
      currentLocation.nOrderId = 5;
      listOfLocations.push(currentLocation);
    } else if(location.indexOf("bed") != -1 && location.indexOf("3") != -1) {
      currentLocation.nOrderId = 6;
      if(sorCodesQuantity > 0) {
      	listOfLocations.push(currentLocation);
      }
    } else {
      currentLocation.nOrderId = orderIdIncrementer;
      orderIdIncrementer++;
      if(sorCodesQuantity > 0) {
      	listOfLocations.push(currentLocation);
      }
    }
  }

  if(cti.store.variables.showFullLocations) {
    for(var i in sorLocations) {
      if(sorLocations[i].nSORQuantity != 0) {
        sorLocations[i].nOrderId = 0;
      }
    }

    sorLocations.sort(compare);
    cti.store.jobs.jobSORLocations = sorLocations;
  } else {
    cti.store.variables.showFullLocations = false;
    for(var i in listOfLocations) {
      if(listOfLocations[i].nSORQuantity != 0) {
        listOfLocations[i].nOrderId = 0;
      }
    }

    listOfLocations.sort(compare);
    cti.store.jobs.jobSORLocations = listOfLocations;
  }
} else {
  delete cti.store.jobs.jobSORLocations;
  cti.store.variables.jobLocations = false;
}

