function fnCalcEstimatedTime(sors) {
  var estimated = 0;

  if(sors.length > 0) {
    for(var i in sors) {
      var sor = sors[i];
      var timeSpentOnCode = parseInt(sor.nQtyDoneThisVisit * sor.nSMVs);
      var timeToSpendOnCode = parseInt(sor.nQtyRemaining * sor.nSMVs);

      estimated += timeSpentOnCode + timeToSpendOnCode;
    }
  }

  if(estimated === 0) {
    if(cti.store.jobs.selectedJob.bJobStartWork) {
      estimated = cti.store.jobs.expectedDurationData.tPairs.asValues[0];
    } else {
      estimated = cti.store.jobs.selectedJob.sExpected_Duration;
    }
  }

  return estimated.toString();
}

function fnCalcCostBanding(jobCost, notifications) {
  var variations = cti.store.jobs.selectedJob.aJobVariationBands;
  var sBandName;
  var sBandOverLimitAction;
  var bandALower;
  var bandAUpper;
  var bandAHeader;
  var bandAAction;

  var bandBLower;
  var bandBUpper;
  var bandBHeader;
  var bandBAction;

  var bandCLower;
  var bandCUpper;
  var bandCHeader;
  var bandCAction;

  for(var i in variations) {
    var thisVariation = variations[i];

    if(thisVariation.sBandName == "Band A") {
      bandALower = thisVariation.nBandLowerLimit;
      bandAUpper = thisVariation.nBandUpperLimit;
      bandAHeader = thisVariation.sBandName;
      bandAAction = thisVariation.sBandOverLimitAction;
    } else if(thisVariation.sBandName == "Band B") {
      bandBLower = thisVariation.nBandLowerLimit;
      bandBUpper = thisVariation.nBandUpperLimit;
      bandBHeader = thisVariation.sBandName;
      bandBAction = thisVariation.sBandOverLimitAction;
    } else if(thisVariation.sBandName == "Band C") {
      bandCLower = thisVariation.nBandLowerLimit;
      bandCUpper = thisVariation.nBandUpperLimit;
      bandCHeader = thisVariation.sBandName;
      bandCAction = thisVariation.sBandOverLimitAction;
    }
  }

  if(bandALower == undefined) bandALower = 0;
  if(bandAUpper == undefined) bandAUpper = 200;
  if(bandAHeader == undefined) bandAHeader = "Band A";
  if(bandAAction == undefined) bandAAction = "Progress with Job";
  if(bandBLower == undefined) bandBLower = 200.01;
  if(bandBUpper == undefined) bandBUpper = 500;
  if(bandBHeader == undefined) bandBHeader = "Band B";
  if(bandBAction == undefined) bandBAction = "Contact Supervisor";
  if(bandCLower == undefined) bandCLower = 500;
  if(bandCUpper == undefined) bandCUpper = 50000;
  if(bandCHeader == undefined) bandCHeader = "Band C";
  if(bandCAction == undefined) bandCAction = "Stop Work";

  var exceededBand = false;

  if(jobCost <= bandAUpper) {
    var title = bandAHeader + ': ' + bandAAction;
    var text = "You are within Band A. Cost Variation Bands for Band A are between £" + bandALower + " and £" + bandAUpper + ".\n\nCurrent Cost on Job: £" + jobCost + ".\n\nYou may progress through the job as standard.";

    if(notifications) {
      cti.utils.callAction(
        "show-notification", {
          "name" : "app_costbanding_a",
          "item-type" : "app-notification",
          "style" : "success",
          "title" : title,
          "text" : text
        }
      );
    }
  } else if(jobCost > bandBLower && jobCost <= bandBUpper) {
    var title = bandBHeader + ': ' + bandBAction;
    var text = "You are within Band B. Cost Variation Bands for Band B are between £" + bandBLower + " and £" + bandBUpper + ".\n\nCurrent Cost on Job: £" + jobCost + ".\n\nPlease contact your supervisor before progressing through the job.";

    if(notifications) {
      cti.utils.callAction(
        "show-notification", {
          "name" : "app_costbanding_b",
          "item-type" : "app-notification",
          "style" : "warning",
          "title" : title,
          "text" : text
        }
      );
    }
  } else if(jobCost > bandCLower) {
    exceededBand = true;
    var title = bandCHeader + ': ' + bandCAction;
    var text = "You are within Band C. Cost Variation Bands for Band C are above £" + bandCLower + ".\n\nCurrent Cost on Job: £" + jobCost + ".\n\nYou may progress through the job, but further addition of SOR Codes or quantity increases to current SOR Codes will now be restricted. Please discuss with Tenant and contact supervisor if required.";

    if(notifications) {
      cti.utils.callAction(
        "show-notification", {
          "name" : "app_costbanding_c",
          "item-type" : "app-notification",
          "style" : "Error",
          "title" : title,
          "text" : text
        }
      );
    }
  }

  return exceededBand;
}

if(startingWork === undefined) { startingWork = false; }
if(minusQty === undefined) { minusQty = false; }

if(cti.store.variables.estimatedOverride === false || cti.store.variables.estimatedOverride === undefined) {
  var sorMins = fnCalcEstimatedTime(cti.store.jobs.selectedJob.aItemGroups);
  cti.fnCalcSMVChange(sorMins);
}

if(minusQty) {
  var minusJobCost = cti.fnCalcJobCost(cti.store.jobs.selectedJob.aItemGroups);
  cti.store.jobs.selectedJob.sPreviousJobCost = minusJobCost;
} else {
  var exceededBand = undefined;
  var costChange = false;
  var jobCost = cti.fnCalcJobCost(cti.store.jobs.selectedJob.aItemGroups);

  if(cti.store.variables.firstCostCalculation) {
    exceededBand = fnCalcCostBanding(jobCost, startingWork);
    delete cti.store.variables.firstCostCalculation;
  } else {
    if(cti.store.jobs.selectedJob.sPreviousJobCost != jobCost) {
      cti.store.jobs.selectedJob.sPreviousJobCost = jobCost;
    }

    exceededBand = fnCalcCostBanding(jobCost, startingWork);
  }

  return exceededBand;
}

var jobCost = 0;

for(var i in sors) {
  var sor = sors[i];
  var jobCostOnCode = (parseFloat(sor.nSellPrice).toFixed(2) * sor.nQtyDoneThisVisit);
  var jobCostToSpendOnCode = (parseFloat(sor.nSellPrice).toFixed(2) * sor.nQtyRemaining);
  var thisSorCost = jobCostOnCode + jobCostToSpendOnCode;

  jobCost += thisSorCost;
}

jobCost = jobCost.toFixed(2);

if(cti.store.jobs.selectedJob.sPreviousJobCost === undefined) {
  cti.store.variables.firstCostCalculation = true;
  cti.store.jobs.selectedJob.sPreviousJobCost = jobCost;
}

return jobCost;
